getColor <- function(chor) {
  Color=ifelse((chor$val1_O3 > 55),2,
               ifelse(((chor$val0_SO2>7)&(chor$val0_NO>37)&(chor$val0_NO2>34)&(chor$val1_PM>24)),2,
                      ifelse(((chor$val3_NO>37)&(chor$val4_NO2>34)&(chor$val1_PM>24)),2,
                             ifelse(((chor$val0_NO>37)&(chor$val1_NO>37)&(chor$val1_NO2>34)&(chor$val0_PM>24)&(chor$val1_PM>24)),2,
                                    ifelse(((chor$val2_NO>37)&(chor$val4_NO>37)&(chor$val1_PM>24)),2,
                                           ifelse((chor$val0_NO>37)&(chor$val0_NO2>34)&(chor$val1_NO2)&(chor$val0_PM)&(chor$val1_PM),2,
                                                ifelse((chor$val0_SO2>7)&(chor$val3_O3)&(chor$val1_PM>24),2,
                                                  ifelse((chor$val0_SO2>7)&(chor$val0_PM>24)&(chor$val2_PM>24),2,
                                                         ifelse((chor$val1_SO2>7)&(chor$val3_NO2>34)&(chor$val2_O3>55),2,
                                                                ifelse((chor$val1_NO>37)&(chor$val2_NO2>34)&(chor$val0_PM>24),2,
                                                                       ifelse((chor$val1_SO2>7)&(chor$val1_NO>37)&(chor$val0_O3>55),2,
                                                                              ifelse((chor$val3_NO>37)&(chor$val4_NO>37)&(chor$val1_NO2>34),2,
                                                                                     ifelse((chor$val3_NO>37)&(chor$val0_NO2>34)&(chor$val2_NO2>34),2,
                                                                                            ifelse((chor$val0_NO2>34)&(chor$val2_O3>55)&(chor$val0_PM>24),2,
                                                                                                   ifelse((chor$val3_NO2>34)&(chor$val1_PM>24),2,
                                                                                                          ifelse((chor$val1_NO2>34)&(chor$val2_O3>55)&(chor$val0_PM>24),2,
                                                                                                                 ifelse((chor$val2_O3>55)&(chor$val1_PM>24)&(chor$val4_PM>24),2,
                                                                                                                        ifelse((chor$val3_NO>37)&(chor$val4_NO>37)&(chor$val2_NO2>34),2,
                                                                                                                               ifelse((chor$val2_O3>55)&(chor$val0_PM>24)&(chor$val2_PM>24),2,
                                                                                                                                      ifelse((chor$val4_SO2>7)&(chor$val0_PM>24),2,
                                                                                                                                             ifelse((chor$val1_SO2>7)&(chor$val2_O3>55)&(chor$val3_O3>55),2,
                                                                                                                                                    ifelse((chor$val1_NO>37)&(chor$val4_O3>55),2,
                                                                                                                                                           ifelse((chor$val4_NO>37)&(chor$val0_O3),2,
                                                                                                                                                                  ifelse((chor$val0_SO2>7)&(chor$val0_O3>55),2,
                                                                                                                                                                         ifelse((chor$val0_O3>55)&(chor$val1_PM),2,
                                                                                                                                                                                ifelse((chor$val0_O3>55)&(chor$val4_O3>55),2,
                                                                                                                                                                                       ifelse((chor$val0_O3>55)&(chor$val0_PM>24),2,)))))))))))))))))))))))))))
  return(Color)
}
